from Crypto.Util.number import long_to_bytes,inverse
from gmpy2 import iroot
from pwn import *
import math,json


def json_recv():
    line = r.recvline()
    return json.loads(line.decode())

def json_send(hsh):
    request = json.dumps(hsh).encode()
    r.sendline(request)

def hex_int(hex):
    return(int(hex,16))
    
def send_X(n):
    json_send({"x":n})
    return(recv(n))

host="20.65.65.163"
port=1003
r = remote(host,port)

#recieve N,c,e
r.recvuntil(")")
data=json_recv()
c=hex_int(data["encrypted_Flag"])
n=hex_int(data["N"])
e=hex_int(data["e"])
print(c,n,e)
#send x=3 and receive data3= p^3-q^3
data3=hex_int(send_X("3"))
#send x=4 and receive data4= p^4-q^4
data4=hex_int(send_X("4"))

#k=p-q
k=math.gcd(data4,data3)
#k1=p+q
k1=int(iroot((k**2)+(4*n),2)[0])
#k1=iroot(2*k1,2)[0]
phi=n-k1+1
d  = inverse(e,phi)
m = pow (c,d,n)
print(long_to_bytes(m))



